import React, { useContext, useState } from "react";
import { AppContext } from "../App";

const Key = ({ keyVal, bigKey, disabled,digit }) => {
  const {gameOver, onSelectLetter, onDelete,onEnter,disabledLetters } = useContext(AppContext);
    const uniqueArray = [...new Set(disabledLetters)];

  const selectLetter = () => {
    if (gameOver.gameOver) return;
    if (keyVal === "ENTER") {
      onEnter();
    } else if (keyVal === "DELETE") {
      onDelete();
    } else {
      onSelectLetter(keyVal);
    }
  };
  return (
    <div
      className="key"
      id={bigKey ? "big" : disabled && `disabled${digit}`}
      onClick={selectLetter}
    >
      {keyVal}
    </div>
  );
}

export default Key;
