import React, { useContext, useState } from "react";
import { AppContext } from "../App";

const Key = ({ keyVal, bigKey, disabled }) => {
  const { board,gameOver,Scoreboard, currAttempt, onSelectLetter, onDelete,onEnter,disabledLetters } = useContext(AppContext);
    const uniqueArray = [...new Set(disabledLetters)];
  const [currentWord , setCurrentWord] = useState("")

  const selectLetter = () => {
    if (gameOver.gameOver) return;
    if (keyVal === "ENTER") {
      onEnter();
      let curWord = "";
        for (let i = 0; i < 5; i++) {
          curWord += board[currAttempt.attempt -1][i];
    }
    setCurrentWord(curWord)
    } else if (keyVal === "DELETE") {
      onDelete();
    } else {
      onSelectLetter(keyVal);
    }
  };
  console.log(currentWord)
  return (
    <div
      className="key"
      id={bigKey ? "big" : disabled && `disabled`}
      onClick={selectLetter}
    >
      {keyVal}
    </div>
  );
}

export default Key;
