import "./App.css";
import Board from "./components/Board";
import Keyboard from "./components/Keyboard";
import { boardDefault, scoreBoardDefault } from "./Words";
import React, { useState, createContext, useEffect } from "react";
import GameOver from "./components/GameOver";

export const AppContext = createContext();

function App() {
  const [board, setBoard] = useState(boardDefault);
  const [Scoreboard, setScoreBoard] = useState(scoreBoardDefault);
  const [score, setScore] = useState([]);
  const [validWord, setValidWord] = useState();
  const [currAttempt, setCurrAttempt] = useState({ attempt: 0, letter: 0 });
  const [correctWord, setCorrectWord] = useState("");
  const [disabledLetters, setDisabledLetters] = useState([]);
  const [gameOver, setGameOver] = useState({
    gameOver: false,
    guessedWord: false,
  });

  const TalkToAPI = async (guess) => {
    try {
      const response = await fetch('https://wordle-apis.vercel.app/api/validate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ guess: guess })
      });
      const data = await response.json();
      setScore(data.score)
      setValidWord(data.is_valid_word)
      console.log(data);
    } catch (error) {
      console.error('Error:', error);
    }
  }

  const validate = () => {
    if (validWord) {
      setCurrAttempt({ attempt: currAttempt.attempt + 1, letter: 0 });
    } else {
      alert("Not a valid word");
    }
  }

  const onEnter = () => {
    if (currAttempt.letter !== 5) {
      return;
    }

    let currWord = "";
    for (let i = 0; i < 5; i++) {
      currWord += board[currAttempt.attempt][i];
    }
    //calling api function to send and get response
    TalkToAPI(currWord)
    console.log(score)
    const totalScore = score.reduce((accumulator, currentValue) => accumulator + currentValue, 0);

    validate()

    if (totalScore === 10) {
      setGameOver({ gameOver: true, guessedWord: true });
      return;
    }
    console.log(currAttempt);
    if (currAttempt.attempt === 5) {
      setGameOver({ gameOver: true, guessedWord: false });
      return;
    }
  };

  const onDelete = () => {
    if (currAttempt.letter === 0) return;
    const newBoard = [...board];
    const newScorebord = [...Scoreboard]
    newBoard[currAttempt.attempt][currAttempt.letter - 1] = "";
    newScorebord[currAttempt.attempt][currAttempt.letter - 1] = 0;
    setBoard(newBoard);
    setScoreBoard(newScorebord)
    setCurrAttempt({ ...currAttempt, letter: currAttempt.letter - 1 });
  };

  const onSelectLetter = (key) => {
    if (currAttempt.letter > 4) return;
    const newBoard = [...board];
    const newScorebord = [...Scoreboard]
    newBoard[currAttempt.attempt][currAttempt.letter] = key+score[currAttempt.letter];
    newScorebord[currAttempt.attempt][currAttempt.letter - 1] = 0;

    setBoard(newBoard);
    setScoreBoard(newScorebord)
    setCurrAttempt({
      attempt: currAttempt.attempt,
      letter: currAttempt.letter + 1,
    });
  };

  return (
    <div className="App">
      <nav>
        <h1>Wordle</h1>
      </nav>
      <AppContext.Provider
        value={{
          score,
          Scoreboard,
          board,
          setBoard,
          currAttempt,
          setCurrAttempt,
          correctWord,
          onSelectLetter,
          onDelete,
          onEnter,
          setDisabledLetters,
          disabledLetters,
          gameOver,
        }}
      >
        <div className="game">
          <Board score={score}/>
          {gameOver.gameOver ? <GameOver /> : <Keyboard />}
        </div>
      </AppContext.Provider>
    </div>
  );
}

export default App;
