import React, { useContext, useState } from "react";
import { AppContext } from "../App";

const Key = ({ keyVal, bigKey, disabled }) => {
  const { keyScore,board,gameOver,Scoreboard, currAttempt, onSelectLetter, onDelete,onEnter,disabledLetters } = useContext(AppContext);
    const uniqueArray = [...new Set(disabledLetters)];
    // console.log(keyScore)
    // console.log(disabledLetters)

    const setKeyNum = ()=>{
      let currentWord = "";
      for (let i = 0; i < 5; i++) {
        currentWord += board[currAttempt.attempt][i];
      }
      console.log(currentWord)
      console.log(Scoreboard)
    }



  const selectLetter = () => {
    if (gameOver.gameOver) return;
    if (keyVal === "ENTER") {
      onEnter();
      setKeyNum();
    } else if (keyVal === "DELETE") {
      onDelete();
    } else {
      onSelectLetter(keyVal);
    }
  };
  return (
    <div
      className="key"
      id={bigKey ? "big" : disabled && `disabled${2}`}
      onClick={selectLetter}
    >
      {keyVal}
    </div>
  );
}

export default Key;
